import Unit from "./Unit"; // 2. Add a new Component called Unit from Unit.js
import "./App.css";
import axios from "axios";
import { useState, useEffect, form } from "react";

function App() {
    const [newUnit, setNewUnit] = useState(units);

    const addUnit = (event) => {
        event.preventDefault();
        const unitObject = {
            code: newUnit,
            title: newUnit,
            offering: newUnit,
        };

        setNewUnit(units.concat(unitObject));
    };

    const handleUnitChange = (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);
        const code = form.get("code");
        const title = form.get("title");
        const offering = [];
        setNewUnit(event.target.value);
    };
    // 4. [stretch goal] Add the array of unit objects below to your application and generate a list of units from the data in the array.
    const units = [
        {
            code: "COMP1010",
            title: "Fundamentals of Computer Science",
            offering: ["S1", "S2"],
        },
        {
            code: "COMP1750",
            title: "Introduction to Business Information Systems",
            offering: ["S1"],
        },
        { code: "COMP2110", title: "Web Technology", offering: ["S1", "S2"] },
        {
            code: "COMP2750",
            title: "Applications Modelling and Development",
            offering: ["S1"],
        },
        { code: "MMCC2045", title: "Interactive Web Design", offering: ["S2"] },
        {
            code: "COMP3120",
            title: "Advanced Web Development",
            offering: ["S2"],
        },
        {
            code: "COMP3130",
            title: "Mobile Application Development",
            offering: ["S1"],
        },
    ];

    return (
        <div className="App">
            <header className="App-header">
                {/* 1. Start with the starter application displaying "COMP3120: Advanced Web Development"  just as a starting point.  */}
                <h1>COMP3120: Advanced Web Development</h1>

                {/* 2. Add a new Component called Unit to display a unit, eg. One you can use as <Unit code="COMP3120" title="Advanced Web Development"> and use this in your main App to display a couple of units.  */}
                <Unit code="COMP3120" title="Advanced Web Development" />

                {/* 4. use the new Component Unit in your main App to display a couple of units and pass units data to component Unit from main App. */}
                <h1>Our Units</h1>
                <form onSubmit={addUnit}>
                    <input value={newUnit} onChange={handleUnitChange}></input>
                    <button type="submit">Save</button>
                </form>
            </header>
            {units.map((u) => (
                <Unit key={u.code} code={u.code} title={u.title} />
            ))}
        </div>
    );
}

export default App;
