import Unit from "./Unit";
import "./App.css";
import axios from "axios";
import React, { useState } from "react";

function App() {
    const units = [
        {
            code: "COMP1010",
            title: "Fundamentals of Computer Science",
            offering: ["S1", "S2"],
        },
        {
            code: "COMP1750",
            title: "Introduction to Business Information Systems",
            offering: ["S1"],
        },
        { code: "COMP2110", title: "Web Technology", offering: ["S1", "S2"] },
        {
            code: "COMP2750",
            title: "Applications Modelling and Development",
            offering: ["S1"],
        },
        { code: "MMCC2045", title: "Interactive Web Design", offering: ["S2"] },
        {
            code: "COMP3120",
            title: "Advanced Web Development",
            offering: ["S2"],
        },
        {
            code: "COMP3130",
            title: "Mobile Application Development",
            offering: ["S1"],
        },
    ];

    const [newUnit, setNewUnit] = useState({ code: "", title: "" });

    const addUnit = (event) => {
        event.preventDefault();
        const unitObject = {
            code: newUnit.code,
            title: newUnit.title,
            offering: [],
        };

        setNewUnit(units.concat(unitObject));
    };

    // const handleUnitChange = (event) => {
    //     event.preventDefault();

    //     // const formData = new FormData(event.target);
    //     // const code = form.get("code");
    //     // const title = form.get("title");
    //     // const offering = [];
    //     setNewUnit(event.target.value);
    // };

    const handleUnitChange = (event) => {
        const { name, value } = event.target;
        setNewUnit((prevUnit) => ({
            code: name === "code" ? value : prevUnit.code,
            title: name === "title" ? value : prevUnit.title,
        }));
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>COMP3120: Advanced Web Development</h1>

                <Unit code="COMP3120" title="Advanced Web Development" />

                <h1>Our Units</h1>
                <form onSubmit={addUnit}>
                    <label>Unit Code</label>
                    <input
                        type="text"
                        name="code"
                        value={newUnit.code}
                        onChange={handleUnitChange}
                    ></input>
                    <label>Unit Title</label>
                    <input
                        type="text"
                        name="title"
                        value={newUnit.title}
                        onChange={handleUnitChange}
                    ></input>
                    <button type="submit">Save</button>
                </form>
            </header>
            {units.map((u) => (
                <Unit key={u.code} code={u.code} title={u.title} />
            ))}
        </div>
    );
}

export default App;
