import Unit from "./Unit";
import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";

import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

const Home = () => {
    return <p>Welcome to our Unit App. You have not logged in</p>;
};

const About = () => {
    return <p>Welcome to our Unit App. You have not logged in</p>;
};

const Units = () => {
    return <p>Welcome to our Unit App. You have not logged in</p>;
};

const Sessions = () => {
    return <p>Welcome to our Unit App. You have not logged in</p>;
};

function App() {
    const [units, setUnits] = useState([]);

    const [newUnitCode, setNewUnitCode] = useState("");
    const [newUnitTitle, setNewUnitTitle] = useState("");

    const addUnit = (event) => {
        event.preventDefault();
        const unitObject = {
            id: units.length,
            code: newUnitCode,
            title: newUnitTitle,
            offering: "S1",
        };
        console.log("unitObject", unitObject);
        axios
            .post("http://localhost:3001/units", unitObject)
            .then((response) => {
                console.log("Post response" + response.data);
                setUnits(units.concat(unitObject));
                setNewUnitCode("");
                setNewUnitTitle("");
                console.log("New unit added");
                console.log("unitObject", unitObject);
            });
    };

    const handleUnitChange = (event) => {
        event.preventDefault();
        const { name, value } = event.target;
        if (name === "code") {
            setNewUnitCode(value);
        } else if (name === "title") {
            setNewUnitTitle(value);
        }
    };

    useEffect(() => {
        console.log("effect is being run");
        axios.get("http://localhost:3001/units").then((response) => {
            // const units = response.data;
            console.log("we have a response", response);
            setUnits(response.data);
        });
    }, []);

    console.log("Rendering the App component");

    return (
        <div className="App">
            <Home></Home>
            <header className="App-header">
                <h1>COMP3120: Advanced Web Development</h1>

                <Unit code="COMP3120" title="Advanced Web Development" />

                <h1>Our Units</h1>
                <form onSubmit={addUnit}>
                    <label>Unit Code</label>
                    <input
                        type="text"
                        name="code"
                        value={newUnitCode}
                        onChange={handleUnitChange}
                    ></input>
                    <label>Unit Title</label>
                    <input
                        type="text"
                        name="title"
                        value={newUnitTitle}
                        onChange={handleUnitChange}
                    ></input>
                    <button type="submit">Save</button>
                </form>
            </header>
            {units.map((u) => (
                <Unit key={u.code} code={u.code} title={u.title} />
            ))}
        </div>
    );
}

export default App;
